import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button, ButtonGroup, InputGroupAddon, InputGroup, Input } from 'reactstrap';
import Messages from "./Messages";

function randomName() {
  const adjectives = ["autumn", "hidden", "bitter", "misty", "silent", "empty", "dry", "dark", "summer", "icy", "delicate", "quiet", "white", "cool", "spring", "winter", "patient", "twilight", "dawn", "crimson", "wispy", "weathered", "blue", "billowing", "broken", "cold", "damp", "falling", "frosty", "green", "long", "late", "lingering", "bold", "little", "morning", "muddy", "old", "red", "rough", "still", "small", "sparkling", "throbbing", "shy", "wandering", "withered", "wild", "black", "young", "holy", "solitary", "fragrant", "aged", "snowy", "proud", "floral", "restless", "divine", "polished", "ancient", "purple", "lively", "nameless"];
  const nouns = ["waterfall", "river", "breeze", "moon", "rain", "wind", "sea", "morning", "snow", "lake", "sunset", "pine", "shadow", "leaf", "dawn", "glitter", "forest", "hill", "cloud", "meadow", "sun", "glade", "bird", "brook", "butterfly", "bush", "dew", "dust", "field", "fire", "flower", "firefly", "feather", "grass", "haze", "mountain", "night", "pond", "darkness", "snowflake", "silence", "sound", "sky", "shape", "surf", "thunder", "violet", "water", "wildflower", "wave", "water", "resonance", "sun", "wood", "dream", "cherry", "tree", "fog", "frost", "voice", "paper", "frog", "smoke", "star"];
  const adjective = adjectives[Math.floor(Math.random() * adjectives.length)];
  const noun = nouns[Math.floor(Math.random() * nouns.length)];
  return adjective + noun;
}

function randomColor() {
  return '#' + Math.floor(Math.random() * 0xFFFFFF).toString(16);
}

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      client_id: '',
      data: null,
      messages: [
        {
          text: "This is a test message!",
          member: {
            color: "blue",
            username: "bluemoon"
          }
        }
      ],
      member: {
        username: randomName(),
        color: randomColor()
      }
    };
    this.sendMessage = this.sendMessage.bind(this);
    this.getMail = this.getMail.bind(this);
  }


  componentDidMount() {
    this.setState({client_id: document.location.host});
    // Call our fetch function below once the component mounts
    this.callBackendAPI()
      .then(res => this.setState({ data: res.express }))
      .catch(err => console.log(err));
    // initiate continuous calls to clients mail box
    this.getMail();
  }

  async sendMessage(){
    console.log(this.state.client_id);
    const body_data = {id: this.state.client_id}
    const response = await fetch('http://localhost:57000/message', {
        method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, cors, *same-origin
        headers: {
            "Content-Type": "text/plain"
            // "Content-Type": "application/x-www-form-urlencoded",
        },
        body: this.state.client_id + '~' + document.getElementById('msg-input').value // body data type must match "Content-Type" header
    });
    const res = await response.json();
    alert(await res.express);
  }

  // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('http://localhost:57000/express_backend', {
      method: "POST", // *GET, POST, PUT, DELETE, etc.
        mode: "cors", // no-cors, cors, *same-origin
        headers: {
            "Content-Type": "text/plain"
        },
        body: document.location.host, // body data type must match "Content-Type" header
    });
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    return body;
  };

  async getMail(){
    const body_data = {id: this.state.client_id}
    const response = await fetch('http://localhost:57000/mail', {
        method: "POST",
        mode: "cors", 
        headers: {"Content-Type": "text/plain"},
        body: this.state.client_id
    });
    const res = await response.json();
    //alert(await res.messages);
    setTimeout(this.getMail, 5000)
  }


  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <p>{document.location.host}</p>
          <p>{this.state.data}</p>

          <Messages
            messages={this.state.messages}
            currentMember={this.state.member}
          />
          <InputGroup>
            <InputGroupAddon addonType="prepend">
              <Button color="primary" onClick={() => this.sendMessage()} >Send</Button>
            </InputGroupAddon>
            <Input id="msg-input"/>
          </InputGroup>
        </header>
      </div>
    );
  }
}

export default App;
