import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Button } from 'reactstrap';


class App extends Component {
  constructor(props){
    state = {
      data: null
    };
    this.onRadioBtnClick = this.onRadioBtnClick.bind(this);
  }

  componentDidMount() {
    // Call our fetch function below once the component mounts
  this.callBackendAPI()
    .then(res => this.setState({ data: res.express }))
    .catch(err => console.log(err));
  }

  async onRadioBtnClick(){
    const response = await fetch('http://localhost:57000/relay_message', {mode: 'cors'});
    const body = await response.json();
    alert(await body);
  }

  // Fetches our GET route from the Express server. (Note the route we are fetching matches the GET route from server.js
  callBackendAPI = async () => {
    const response = await fetch('http://localhost:57000/express_backend', {mode: 'cors'});
    const body = await response.json();

    if (response.status !== 200) {
      throw Error(body.message) 
    }
    return body;
  };


  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
          <p>{document.location.host}</p>
          <p>{this.state.data}</p>
          <Button color="primary" onClick={() => this.onRadioBtnClick()} >One</Button>
        </header>
      </div>
    );
  }
}

export default App;
